import React, { Fragment } from "react";
import "./QuestionList.css";
//Import Components
import Alert from "@material-ui/lab/Alert";
import ScrollableTable from "../../components/ScrollableTable";
import SearchBar from "../../components/SearchBar/SearchBar";

const columns = [
  {
    id: "id",
    label: "ID",
  },
  {
    id: "name",
    label: "Name",
  },
  {
    id: "email",
    label: "Email",
  },
  {
    id: "date",
    label: "Date",
  },
  {
    id: "observations",
    label: "Observations",
  },
  {
    id: "creationDate",
    label: "Creation Date",
  },
];

class QuestionList extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      questionList: [],
      errorAlert: false,
      errorMessage: "",
    };
  }

  componentDidMount() {
    this.fetchQuestionList();
  }

  componentWillUnmount() {
    this.setState({ questionList: [], errorAlert: false, errorMessage: "" });
  }

  handleErrors(response) {
    console.log(response);
    if (!response.ok) {
      throw Error(
        "An Internal Error has occurred. Please retry the operation."
      );
    }
    return response;
  }

  fetchQuestionList = (id, page) => {
    let url = process.env.REACT_APP_SERVER_URL + "/questions";

    if (id) {
      url += `?id=${id}`;
    } else if (page) {
      url += `?page=${page}`;
    }

    fetch(url)
      .then(this.handleErrors)
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          questionList: result,
          errorAlert: false,
          errorMessage: "",
        });
      })
      .catch((error) => {
        this.setState({
          errorAlert: true,
          errorMessage: error.message,
        });
      });
  };

  render() {
    const questionList = this.state.questionList;

    return (
      <Fragment>
        {this.state.errorAlert && (
          <Alert severity="error">{this.state.errorMessage}</Alert>
        )}
        <br></br>
        <SearchBar fetchData={this.fetchQuestionList} />
        <br></br>
        <ScrollableTable columns={columns} rows={questionList} />
      </Fragment>
    );
  }
}

export default QuestionList;
