import React, { Fragment } from "react";
import "./QuestionList.css";
//Import Components
import Alert from "@material-ui/lab/Alert";
import ScrollableTable from "../../components/ScrollableTable";

// const mockQuestions = [
//   {
//     id: "5feb6904f827835ed827ddeb",
//     name: "Who am I?",
//     email: "who@sky.world",
//     date: "2020-12-31T00:00:00.000Z",
//     observations: "Who knows.",
//     creationDate: "2020-12-29T17:36:04.933Z",
//   },
//   {
//     id: "5feb6904f827835ed827ddeb",
//     name: "Who am I?",
//     email: "who@sky.world",
//     date: "2020-12-31T00:00:00.000Z",
//     observations: "Who knows.",
//     creationDate: "2020-12-29T17:36:04.933Z",
//   },
// ];

const columns = [
  {
    id: "name",
    label: "Name",
  },
  {
    id: "email",
    label: "Email",
  },
  {
    id: "date",
    label: "Date",
  },
  {
    id: "observations",
    label: "Observations",
  },
  {
    id: "creationDate",
    label: "Creation Date",
  },
];

class QuestionList extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      questionList: [],
      errorAlert: false,
      errorMessage: "",
    };
  }

  componentDidMount() {
    this.fetchQuestionList();
  }

  componentWillUnmount() {
    this.setState({ questionList: [] });
  }

  handleErrors(response) {
    if (!response.ok)
    {
        throw Error("An Internal Error has occurred. Please retry the operation.");
    }
       // throw Error();
    return response;
}

  fetchQuestionList = (id, page) => {
    let url = process.env.REACT_APP_SERVER_URL + "/questions";

    if (id) {
      url += `?id=${id}`;
    } else if (page) {
      url += `?page=${page}`;
    }

    fetch(url)
        .then(this.handleErrors)
        .then((res) => res.json())
      .then((result) => {
        this.setState({ questionList: result });
      })
      .catch(error => {
        console.log("ERROR: ", error.message);
        console.log(this)

        this.setState({
            errorAlert: true,
            errorMessage: error.error,
        });
      });
  };

  render() {
    const questionList = this.state.questionList;

    console.log("Questions: ", questionList);
    console.log("ErrorAlert: ", this.state.errorAlert);
    console.log("ErrorMessage: ", this.state.errorMessage);

    return (
      <Fragment>
        {this.state.errorAlert && (
          <Alert severity="error">{this.state.errorMessage}</Alert>
        )}
        <ScrollableTable columns={columns} rows={questionList} />
      </Fragment>
    );
  }
}

export default QuestionList;
