import React, { Fragment } from "react";
import "./QuestionForm.css"
//Import Components
import { Form, Field } from "react-final-form";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

//Adapted from: https://final-form.org/docs/react-final-form/examples/simple

const DatePickerAdapter = ({ input: { onChange, value }, ...rest }) => (
    <DatePicker selected={value} onChange={date => onChange(date)} {...rest} />
  );

class QuestionForm extends React.Component {
  postQuestion = () => {
    console.log("Question Posted!");
  };

  render() {
    return (
      <div class="form-container">
        <h1>Submit Question</h1>
        <Form
          class="form"
          onSubmit={this.postQuestion}
          render={({ handleSubmit, form, submitting, pristine }) => (
            <form onSubmit={handleSubmit}>
              <div>
                <label>Name</label>
                <Field
                  name="name"
                  component="input"
                  type="text"
                  placeholder="Name"
                />
              </div>
              <div>
                <label>Email</label>
                <Field
                  name="email"
                  component="input"
                  type="text"
                  placeholder="Email"
                />
              </div>
              <div>
              <label>Date</label>
              <Field
                name="date"
                dateFormat="yyyy/MM/dd"
                component={DatePickerAdapter}
              />
            </div>
            <div>
                <label>Observations</label>
                <Field
                  name="observations"
                  component="input"
                  type="text"
                  placeholder="Observations"
                />
              </div>
              <div className="buttons">
                <button type="submit" disabled={submitting || pristine}>
                  Submit
                </button>
                <button
                  type="button"
                  onClick={form.reset}
                  disabled={submitting || pristine}
                >
                  Reset
                </button>
              </div>
            </form>
          )}
        />
      </div>
    );
  }
}

export default QuestionForm;
