import React from "react";
import "./QuestionList.css";
//Import Components
import ScrollableTable from "../../components/ScrollableTable";
import QuestionListItem from "./QuestionListItem";

const mockQuestions = [
  {
    id: "5feb6904f827835ed827ddeb",
    name: "Who am I?",
    email: "who@sky.world",
    date: "2020-12-31T00:00:00.000Z",
    observations: "Who knows.",
    creationDate: "2020-12-29T17:36:04.933Z",
  },
  {
    id: "5feb6904f827835ed827ddeb",
    name: "Who am I?",
    email: "who@sky.world",
    date: "2020-12-31T00:00:00.000Z",
    observations: "Who knows.",
    creationDate: "2020-12-29T17:36:04.933Z",
  },
];

const columns = [
  {
    id: "name",
    label: "Name"
  },
  {
    id: "email",
    label: "Email"
  },
  {
    id: "date",
    label: "Date"
  },
  {
    id: "observations",
    label: "Observations"
  },
  {
    id: "creationDate",
    label: "Creation Date"
  },
];

class QuestionList extends React.Component {
    constructor(props){
        super(props);

        this.state={
            questionList:[]
        };
    }

    componentDidMount() {
        fetch("http://localhost:3000/questions").then(res => res.json()).then(result => {this.setState({questionList:result})}); 
    }

    render() {
        console.log("Questions: ", this.state.questionList)
        return (
            <ScrollableTable columns={columns} rows={mockQuestions} />
        )
    }
}

export default QuestionList;
