const columns = [
  {
    id: "name",
    label: "Name",
  },
  {
    id: "email",
    label: "Email",
  },
  {
    id: "date",
    label: "Date",
  },
  {
    id: "observations",
    label: "Observations",
  },
  {
    id: "creationDate",
    label: "Creation Date",
  },
];

class QuestionList extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      questionList: [],
    };
  }

  componentDidMount() {
   
  }

  componentWillUnmount() {
    this.setState({ questionList: [] });
  }

  fetchQuestionList = ({id, page}) => {
      try{
        fetch(process.env.REACT_APP_SERVER_URL + "/questions")
        .then((res) => res.json())
        .then((result) => {
          this.setState({ questionList: result });
        });
      } catch (error) {
        switch (error.response.status) {
            case 400:
            case 401:
                errorMessage = translator.errorMessage(error);
                break;
            default:
                errorMessage = translation.errors.default;
        }
      }
  }

  render() {
    const questionList = this.state.questionList;

    console.log(questionList);

    return <ScrollableTable columns={columns} rows={questionList} />;
  }
}

export default QuestionList;
