import React from "react";
import "./QuestionForm.css";
//Import Components
import { Form, Field } from "react-final-form";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {
  composeValidators,
  required,
  emailFormat,
  greaterThanTomorrow,
} from "./Validators";

//Adapted from: https://final-form.org/docs/react-final-form/examples/simple

const DatePickerAdapter = ({ input: { onChange, value }, ...rest }) => (
  <DatePicker selected={value} onChange={(date) => onChange(date)} {...rest} />
);

class QuestionForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      hasErrors: false,
    };
  }

  postQuestion = (a) => {
    console.log("Question Posted!");
    console.log(a);
  };


  render() {
    return (
      <div className="form-container">
        <h2>Submit Question</h2>
        <Form
          onSubmit={this.postQuestion}
          render={({ handleSubmit, form, submitting, pristine }) => (
            <form onSubmit={handleSubmit} className="form">
              <div className="form-item">
                <label>Name</label>
                <Field name="name" validate={required}>
                  {({ input, meta }) => {
                    return (
                      <div>
                        <input
                          {...input}
                          type="text"
                          placeholder="Name"
                          style={{ width: "100%" }}
                        />
                        {meta.error && meta.touched && (
                          <div className="error">{meta.error}</div>
                        )}
                      </div>
                    );
                  }}
                </Field>
              </div>
              <div className="form-item">
                <label>Email</label>
                <Field
                  name="email"
                  validate={composeValidators(required, emailFormat)}
                >
                  {({ input, meta }) => {
                      console.log(form.getState());
                    return (
                      <div>
                        <input
                          {...input}
                          type="text"
                          placeholder="Name"
                          style={{ width: "100%" }}
                        />
                        {meta.error && meta.touched && (
                          <div className="error">{meta.error}</div>
                        )}
                      </div>
                    );
                  }}
                </Field>
              </div>
              <div className="form-item">
                <label>Date</label>
                <Field
                  name="date"
                  dateFormat="yyyy/MM/dd"
                  validate={composeValidators(required, greaterThanTomorrow)}
                  component={DatePickerAdapter}
                >
                  {({ input, meta }) => {
                      console.log("MEATA: ", meta);
                    return (
                      <div>
                        <DatePicker
                          dateFormat="yyyy/MM/dd"
                          selected={input.value}
                          onChange={(date) => input.onChange(date)}
                        />
                        {meta.error && meta.dirty && (
                          <div className="error">{meta.error}</div>
                        )}
                      </div>
                    );
                  }}
                </Field>
              </div>
              <div className="form-item">
                <label>Observations</label>
                <Field
                  name="observations"
                  component="textarea"
                  type="text"
                  placeholder="Observations"
                />
              </div>

              <div className="buttons">
                <button type="submit" disabled={submitting || pristine}>
                  Submit
                </button>
                <button
                  type="button"
                  onClick={form.reset}
                  disabled={() => {
                      let formState = form.getState();
                      console.log("FORM STATE: ", formState);
                      return ((formState.errors && formState.errors.length > 0));
                  }}
                >
                  Reset
                </button>
              </div>
            </form>
          )}
        />
      </div>
    );
  }
}

export default QuestionForm;
